mapper.select:
SELECT
-- 级联表的字段 ,qiudui.qiudui_name as qiuduiName ,qiudui.qiudui_types as qiuduiTypes ,qiudui.qiudui_address as qiuduiAddress ,qiudui.jiaolian_id as qiuduiJiaolianId ,qiudui.qiudui_content as qiuduiContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM jiarudeqiudui a left JOIN qiudui qiudui ON a.qiudui_id = qiudui.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.qiudui_id = #{params.qiuduiId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.jiarudeqiudui_number >= #{params.jiarudeqiuduiNumberStart}
and a.jiarudeqiudui_number <= #{params.jiarudeqiuduiNumberEnd}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.qiudui_id IS NOT NULL
and qiudui.qiudui_name like CONCAT('%',#{params.qiuduiName},'%')
and qiudui.qiudui_types = #{params.qiuduiTypes}
and qiudui.qiudui_address like CONCAT('%',#{params.qiuduiAddress},'%')
and qiudui.jiaolian_id = #{params.jiaolianId}
and qiudui.qiudui_content like CONCAT('%',#{params.qiuduiContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
